export interface Airport {
  code: string;
  name: string;
  city: string;
  country: string;
}

export const airports: Airport[] = [
  { code: "SYD", name: "Sydney Airport", city: "Sydney", country: "Australia" },
  { code: "MEL", name: "Melbourne Airport (Tullamarine)", city: "Melbourne", country: "Australia" },
  { code: "LAX", name: "Los Angeles International Airport", city: "Los Angeles", country: "USA" },
  { code: "JFK", name: "John F. Kennedy International Airport", city: "New York", country: "USA" },
  { code: "LHR", name: "Heathrow Airport", city: "London", country: "UK" },
  { code: "CDG", name: "Charles de Gaulle Airport", city: "Paris", country: "France" },
  { code: "ATL", name: "Hartsfield–Jackson Atlanta International Airport", city: "Atlanta", country: "USA" },
  { code: "DXB", name: "Dubai International Airport", city: "Dubai", country: "UAE" },
  { code: "HKG", name: "Hong Kong International Airport", city: "Hong Kong", country: "Hong Kong" },
  { code: "BNE", name: "Brisbane Airport", city: "Brisbane", country: "Australia" },
  { code: "PER", name: "Perth Airport", city: "Perth", country: "Australia" },
  { code: "DFW", name: "Dallas Fort Worth International Airport", city: "Dallas", country: "USA" },
];

import { useState, useRef, useEffect } from "react";
import type { Airport } from "../data/airports";

interface AutocompleteProps {
  airports: Airport[];
  value: Airport | null;
  onChange: (airport: Airport | null) => void;
  placeholder?: string;
  testId?: string;
}

export function Autocomplete({
  airports,
  value,
  onChange,
  placeholder = "Select airport",
  testId,
}: AutocompleteProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        containerRef.current &&
        !containerRef.current.contains(event.target as Node)
      ) {
        setIsOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const filteredAirports = airports.filter((airport) => {
    const search = searchTerm.toLowerCase();
    return (
      airport.code.toLowerCase().includes(search) ||
      airport.name.toLowerCase().includes(search) ||
      airport.city.toLowerCase().includes(search) ||
      airport.country.toLowerCase().includes(search)
    );
  });

  const handleSelect = (airport: Airport) => {
    onChange(airport);
    setIsOpen(false);
    setSearchTerm("");
  };

  return (
    <div ref={containerRef} style={{ position: "relative", width: "100%" }}>
      <button
        data-testid={testId}
        onClick={() => setIsOpen(!isOpen)}
        style={{
          width: "100%",
          padding: "10px",
          border: "1px solid #ccc",
          borderRadius: "4px",
          backgroundColor: "white",
          textAlign: "left",
          cursor: "pointer",
          fontSize: "14px",
        }}
      >
        {value ? `${value.code} - ${value.name}` : placeholder}
      </button>

      {isOpen && (
        <div
          style={{
            position: "absolute",
            top: "100%",
            left: 0,
            right: 0,
            marginTop: "4px",
            backgroundColor: "white",
            border: "1px solid #ccc",
            borderRadius: "4px",
            maxHeight: "300px",
            overflowY: "auto",
            zIndex: 1000,
            boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
          }}
        >
          <div style={{ padding: "8px" }}>
            <input
              type="text"
              placeholder="Search airports..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={{
                width: "100%",
                padding: "8px",
                border: "1px solid #ccc",
                borderRadius: "4px",
                fontSize: "14px",
              }}
              autoFocus
            />
          </div>

          <div>
            {filteredAirports.map((airport) => (
              <button
                key={airport.code}
                data-testid={airport.code}
                onClick={() => handleSelect(airport)}
                style={{
                  width: "100%",
                  padding: "10px",
                  border: "none",
                  backgroundColor: "white",
                  textAlign: "left",
                  cursor: "pointer",
                  fontSize: "14px",
                  borderTop: "1px solid #f0f0f0",
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.backgroundColor = "#f5f5f5";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.backgroundColor = "white";
                }}
              >
                <div style={{ fontWeight: "bold" }}>{airport.code}</div>
                <div style={{ fontSize: "12px", color: "#666" }}>
                  {airport.name}, {airport.city}, {airport.country}
                </div>
              </button>
            ))}

            {filteredAirports.length === 0 && (
              <div
                style={{
                  padding: "10px",
                  textAlign: "center",
                  color: "#666",
                  fontSize: "14px",
                }}
              >
                No airports found
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

interface ToggleButtonProps {
  options: Array<{ value: string; label: string; testId?: string }>;
  value: string;
  onChange: (value: string) => void;
}

export function ToggleButton({ options, value, onChange }: ToggleButtonProps) {
  return (
    <div
      style={{
        display: "inline-flex",
        border: "1px solid #ccc",
        borderRadius: "4px",
        overflow: "hidden",
      }}
    >
      {options.map((option) => (
        <button
          key={option.value}
          data-testid={option.testId}
          onClick={() => onChange(option.value)}
          style={{
            padding: "10px 20px",
            border: "none",
            backgroundColor: value === option.value ? "#007bff" : "white",
            color: value === option.value ? "white" : "#333",
            cursor: "pointer",
            fontSize: "14px",
            fontWeight: value === option.value ? "bold" : "normal",
            borderRight:
              option !== options[options.length - 1]
                ? "1px solid #ccc"
                : "none",
            transition: "all 0.2s",
          }}
          onMouseEnter={(e) => {
            if (value !== option.value) {
              e.currentTarget.style.backgroundColor = "#f5f5f5";
            }
          }}
          onMouseLeave={(e) => {
            if (value !== option.value) {
              e.currentTarget.style.backgroundColor = "white";
            }
          }}
        >
          {option.label}
        </button>
      ))}
    </div>
  );
}

interface CalendarProps {
  selectedDate: Date | null;
  onSelectDate: (date: Date) => void;
  minDate?: Date;
}

export function Calendar({ selectedDate, onSelectDate, minDate }: CalendarProps) {
  const today = new Date();
  today.setHours(0, 0, 0, 0);

  const currentMonth = selectedDate || today;
  const year = currentMonth.getFullYear();
  const month = currentMonth.getMonth();

  const firstDayOfMonth = new Date(year, month, 1);
  const lastDayOfMonth = new Date(year, month + 1, 0);
  const firstDayOfWeek = firstDayOfMonth.getDay();
  const daysInMonth = lastDayOfMonth.getDate();

  const monthNames = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  const dayNames = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

  const isDateDisabled = (date: Date) => {
    if (minDate) {
      const compareDate = new Date(date);
      compareDate.setHours(0, 0, 0, 0);
      const compareMinDate = new Date(minDate);
      compareMinDate.setHours(0, 0, 0, 0);
      return compareDate < compareMinDate;
    }
    return date < today;
  };

  const isSameDay = (date1: Date | null, date2: Date) => {
    if (!date1) return false;
    return (
      date1.getDate() === date2.getDate() &&
      date1.getMonth() === date2.getMonth() &&
      date1.getFullYear() === date2.getFullYear()
    );
  };

  const renderDays = () => {
    const days = [];

    // Empty cells for days before the first day of the month
    for (let i = 0; i < firstDayOfWeek; i++) {
      days.push(
        <div
          key={`empty-${i}`}
          style={{
            width: "40px",
            height: "40px",
          }}
        />
      );
    }

    // Days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(year, month, day);
      const disabled = isDateDisabled(date);
      const selected = isSameDay(selectedDate, date);

      days.push(
        <button
          key={day}
          data-testid={`date-${day}`}
          onClick={() => !disabled && onSelectDate(date)}
          disabled={disabled}
          style={{
            width: "40px",
            height: "40px",
            border: "none",
            backgroundColor: selected ? "#007bff" : "transparent",
            color: disabled ? "#ccc" : selected ? "white" : "#333",
            cursor: disabled ? "not-allowed" : "pointer",
            borderRadius: "4px",
            fontSize: "14px",
            fontWeight: selected ? "bold" : "normal",
          }}
          onMouseEnter={(e) => {
            if (!disabled && !selected) {
              e.currentTarget.style.backgroundColor = "#f5f5f5";
            }
          }}
          onMouseLeave={(e) => {
            if (!disabled && !selected) {
              e.currentTarget.style.backgroundColor = "transparent";
            }
          }}
        >
          {day}
        </button>
      );
    }

    return days;
  };

  return (
    <div
      style={{
        padding: "16px",
        backgroundColor: "white",
        borderRadius: "4px",
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          marginBottom: "16px",
          fontSize: "16px",
          fontWeight: "bold",
        }}
      >
        {monthNames[month]} {year}
      </div>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(7, 40px)",
          gap: "4px",
          marginBottom: "8px",
        }}
      >
        {dayNames.map((day) => (
          <div
            key={day}
            style={{
              width: "40px",
              height: "30px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontSize: "12px",
              fontWeight: "bold",
              color: "#666",
            }}
          >
            {day}
          </div>
        ))}
      </div>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(7, 40px)",
          gap: "4px",
        }}
      >
        {renderDays()}
      </div>
    </div>
  );
}

import { useState, useRef, useEffect } from "react";
import { Calendar } from "./Calendar";

interface DatePickerProps {
  selectedDate: Date | null;
  onSelectDate: (date: Date) => void;
  placeholder?: string;
  testId?: string;
  minDate?: Date;
}

export function DatePicker({
  selectedDate,
  onSelectDate,
  placeholder = "Select date",
  testId,
  minDate,
}: DatePickerProps) {
  const [isOpen, setIsOpen] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        containerRef.current &&
        !containerRef.current.contains(event.target as Node)
      ) {
        setIsOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const formatDate = (date: Date) => {
    const options: Intl.DateTimeFormatOptions = {
      year: "numeric",
      month: "short",
      day: "numeric",
    };
    return date.toLocaleDateString("en-US", options);
  };

  const handleSelectDate = (date: Date) => {
    onSelectDate(date);
    setIsOpen(false);
  };

  return (
    <div ref={containerRef} style={{ position: "relative", width: "100%" }}>
      <button
        data-testid={testId}
        onClick={() => setIsOpen(!isOpen)}
        style={{
          width: "100%",
          padding: "10px",
          border: "1px solid #ccc",
          borderRadius: "4px",
          backgroundColor: "white",
          textAlign: "left",
          cursor: "pointer",
          fontSize: "14px",
        }}
      >
        {selectedDate ? formatDate(selectedDate) : placeholder}
      </button>

      {isOpen && (
        <div
          style={{
            position: "absolute",
            top: "100%",
            left: 0,
            marginTop: "4px",
            backgroundColor: "white",
            border: "1px solid #ccc",
            borderRadius: "4px",
            zIndex: 1000,
            boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
          }}
        >
          <Calendar
            selectedDate={selectedDate}
            onSelectDate={handleSelectDate}
            minDate={minDate}
          />
        </div>
      )}
    </div>
  );
}

import { useState } from "react";
import { Autocomplete } from "./Autocomplete";
import { ToggleButton } from "./ToggleButton";
import { DatePicker } from "./DatePicker";
import { airports } from "../data/airports";
import type { Airport } from "../data/airports";
import "./FlightBooking.css";

interface FlightBookingProps {
  onSubmit?: () => void;
}

export default function FlightBooking({ onSubmit }: FlightBookingProps) {
  const [tripType, setTripType] = useState<"one-way" | "return">("return");
  const [fromAirport, setFromAirport] = useState<Airport | null>(null);
  const [toAirport, setToAirport] = useState<Airport | null>(null);
  const [departureDate, setDepartureDate] = useState<Date | null>(null);
  const [returnDate, setReturnDate] = useState<Date | null>(null);

  const handleSubmit = () => {
    if (onSubmit) {
      onSubmit();
    }
  };

  return (
    <div className="flight-booking">
      <h1 className="flight-booking__title">Book Your Flight</h1>

      <div className="flight-booking__form">
        <div className="flight-booking__section">
          <label className="flight-booking__label">Trip Type</label>
          <ToggleButton
            options={[
              { value: "one-way", label: "One Way", testId: "one-way" },
              { value: "return", label: "Return", testId: "return" },
            ]}
            value={tripType}
            onChange={(value) => {
              setTripType(value as "one-way" | "return");
              if (value === "one-way") {
                setReturnDate(null);
              }
            }}
          />
        </div>

        <div className="flight-booking__section">
          <label className="flight-booking__label">From</label>
          <Autocomplete
            airports={airports}
            value={fromAirport}
            onChange={setFromAirport}
            placeholder="Select departure airport"
            testId="airport-from"
          />
        </div>

        <div className="flight-booking__section">
          <label className="flight-booking__label">To</label>
          <Autocomplete
            airports={airports}
            value={toAirport}
            onChange={setToAirport}
            placeholder="Select destination airport"
            testId="airport-to"
          />
        </div>

        <div className="flight-booking__dates">
          <div className="flight-booking__section flight-booking__section--half">
            <label className="flight-booking__label">Departure Date</label>
            <DatePicker
              selectedDate={departureDate}
              onSelectDate={setDepartureDate}
              placeholder="Select departure date"
              testId="datepicker-trigger-departure"
            />
          </div>

          {tripType === "return" && (
            <div className="flight-booking__section flight-booking__section--half">
              <label className="flight-booking__label">Return Date</label>
              <DatePicker
                selectedDate={returnDate}
                onSelectDate={setReturnDate}
                placeholder="Select return date"
                testId="datepicker-trigger-return"
                minDate={departureDate || undefined}
              />
            </div>
          )}
        </div>

        <div className="flight-booking__section">
          <button
            data-testid="submit"
            onClick={handleSubmit}
            className="flight-booking__submit"
          >
            Search Flights
          </button>
        </div>
      </div>
    </div>
  );
}

.flight-booking {
  max-width: 600px;
  margin: 40px auto;
  padding: 24px;
  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
}

.flight-booking__title {
  font-size: 28px;
  font-weight: bold;
  margin-bottom: 24px;
  color: #333;
}

.flight-booking__form {
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.flight-booking__section {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.flight-booking__section--half {
  flex: 1;
  min-width: 0;
}

.flight-booking__label {
  font-size: 14px;
  font-weight: 600;
  color: #333;
}

.flight-booking__dates {
  display: flex;
  gap: 16px;
}

.flight-booking__submit {
  width: 100%;
  padding: 12px 24px;
  background-color: #28a745;
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 16px;
  font-weight: bold;
  cursor: pointer;
  transition: background-color 0.2s;
}

.flight-booking__submit:hover {
  background-color: #218838;
}

.flight-booking__submit:active {
  background-color: #1e7e34;
}
