        <Example.Item title="without backgroundSlot, size is based on the parent component">
            <div style={{ height: 300, position: "relative" }}>
                <Scrim>Text</Scrim>
            </div>
        </Example.Item>
    </Example>
);

const nativeRender = () => (
    <Example>
        <Example.Item title="native with options prop">
            <Select
                name="animal"
                id="animal-1"
                placeholder="Select an animal"
                options={[
                    { label: "Dog", value: "dog" },
                    { label: "Turtle", value: "turtle" },
                ]}
                inputAttributes={{
                    "aria-label": "Select an animal",
                }}
            />
        </Example.Item>
        <Example.Item title="native with option tags">
            <Select
                name="animal"
                id="animal-2"
                placeholder="Select an animal"
                inputAttributes={{
                    "aria-label": "Select an animal",
                }}
            >
                <option value="dog">Dog</option>
                <option value="turtle">Turtle</option>
            </Select>
        </Example.Item>
    </Example>
);

const triggerOnly = (args) => {
    const toggle = useToggle();
    const [value, setValue] = React.useState("Dog");

    const handleClick: SelectProps["onClick"] = (e) => {
        args.handleClick(e);
        toggle.toggle();
    };

    return (
        <Example>
            <Example.Item title="trigger only, onClick">
                <Select
                    name="animal"
                    placeholder="Select an animal"
                    onClick={handleClick}
                    value="dog"
                    inputAttributes={{
                        "aria-label": "Select an animal",
                    }}
                >
                    {value}
                </Select>
                <Modal
                    active={toggle.active}
                    onClose={toggle.deactivate}
                    position="bottom"
                    padding={2}
                    attributes={{ "aria-label": "Select an animal" }}
                >
                    <div role="listbox" aria-label="Select an animal">
                        <MenuItem
                            roundedCorners
                            onClick={() => {
                                setValue("Dog");
                                toggle.deactivate();
                            }}
                            attributes={{
                                role: "option",
                            }}
                        >
                            Dog
                        </MenuItem>
                        <MenuItem
                            roundedCorners
                            attributes={{
                                role: "option",
                            }}
                            onClick={() => {
                                setValue("Turtle");
                                toggle.deactivate();
                            }}
                        >
                            Turtle
                        </MenuItem>
                    </div>
                </Modal>
            </Example.Item>
        </Example>
    );
};

const renderValue = () => {
    const options = [
        {
            value: "1",
            label: "Title 1",
            subtitle: "Subtitle 1",
        },
        {
            value: "2",
            label: "Title 2",
            subtitle: "Subtitle 2",
        },
    ];

    return (
        <Example>
            <Example.Item title="default renderer, single">
                <Select.Custom name="animal" defaultValue="1" placeholder="Nothing selected">
                    {options.map((option) => (
                        <Select.Option key={option.value} value={option.value}>
                            <Text weight="medium">{option.label}</Text>
                            <Text weight="regular" color="neutral-faded">
                                {option.subtitle}
                            </Text>
                        </Select.Option>
                    ))}
                </Select.Custom>
            </Example.Item>

            <Example.Item title="default renderer, multiple">
                <Select.Custom name="animal" defaultValue={["1"]} multiple placeholder="Nothing selected">
                    {options.map((option) => (
                        <Select.Option key={option.value} value={option.value}>
                            <Text weight="medium">{option.label}</Text>
                            <Text weight="regular" color="neutral-faded">
                                {option.subtitle}
                            </Text>
                        </Select.Option>
                    ))}
                </Select.Custom>
            </Example.Item>

            <Example.Item title="renderValue, single">
                <Select.Custom
                    name="animal"
                    defaultValue="1"
                    placeholder="Nothing selected"
                    renderValue={(args) => <Badge>Title {args.value}</Badge>}
                >
                    {options.map((option) => (
                        <Select.Option key={option.value} value={option.value}>
                            <Text weight="medium">{option.label}</Text>
                            <Text weight="regular" color="neutral-faded">
                                {option.subtitle}
                            </Text>
                        </Select.Option>
                    ))}
                </Select.Custom>
            </Example.Item>

            <Example.Item title="renderValue, multiple">
                <Select.Custom
                    name="animal"
                    defaultValue={["1"]}
                    multiple
                    placeholder="Nothing selected"
                    renderValue={(args) => <Badge>Titles {args.value.join(", ")}</Badge>}
                >
                    {options.map((option) => (
                        <Select.Option key={option.value} value={option.value}>
                            <Text weight="medium">{option.label}</Text>
                            <Text weight="regular" color="neutral-faded">
                                {option.subtitle}
                            </Text>
                        </Select.Option>
                    ))}
                </Select.Custom>
            </Example.Item>
        </Example>
    );
};

const error = () => (
    <Example>
        <Example.Item title="error">
            <Select.Custom name="animal" placeholder="Select an animal" hasError>
                <Select.Option value="dog">Dog</Select.Option>
                <Select.Option value="turtle">Turtle</Select.Option>
            </Select.Custom>
        </Example.Item>
    </Example>
);

const fallback = () => (
