     1→export type SelectionMode = "date" | "month";
     2→export type BaseProps = {
     3→    /** Default month to display. Used in uncontrolled mode to provide the initial month and then updated by the component internally
     4→     * @default Date.now()
     5→     */
     6→    defaultMonth?: Date;
     7→    /** Month to display. Used in controlled mode and should be updated using the `onMonthChange` callback */
     8→    month?: Date;
     9→    /** Callback when the month changes */
    10→    onMonthChange?: (args: {
    11→        date: Date;
    12→    }) => void;
    13→    /** Minimum date that can be selected */
    14→    min?: Date;
    15→    /** Maximum date that can be selected */
    16→    max?: Date;
    17→    /** Number of months to render at the same time
    18→     * @default 1
    19→     */
    20→    monthsToRender?: number;
    21→    /** First day of the week
    22→     * @default 1, Monday
    23→     */
    24→    firstWeekDay?: number;
    25→    /** Dates that are selected */
    26→    selectedDates?: Date[];
    27→    /** Dates that are disabled */
    28→    disabledDates?: Date[];
    29→    /** Render a custom weekday label, can be used for localization */
    30→    renderWeekDay?: (args: {
    31→        weekDay: number;
    32→        date: Date;
    33→    }) => string;
    34→    /** Render a custom month label, can be used for localization */
    35→    renderSelectedMonthLabel?: (args: {
    36→        date: Date;
    37→    }) => string;
    38→    /** Render a custom month label, can be used for localization */
    39→    renderMonthLabel?: (args: {
    40→        month: number;
    41→        date: Date;
    42→    }) => string;
    43→    /** Render a custom date slot content under the date number */
    44→    renderDateSlot?: (args: {
    45→        date: Date;
    46→        selected: boolean;
    47→    }) => React.ReactNode;
    48→    /** Aria label for the previous month button */
    49→    previousMonthAriaLabel?: string;
