    selectedColor?: ToggleButtonProps["selectedColor"];
    /** Callback when the toggle button group value changes */
    onChange?: (args: {
        value: string[];
        event: React.MouseEvent<HTMLElement> | React.KeyboardEvent<HTMLElement>;
