    10→export type CloseReason = "escape-key" | "outside-click"
    11→/**
    12→ * Keeping the item selection type here since DropdownMenu items use Flyout context
    13→ * and not a separate DropdownMenu context
    14→ */
    15→ | "item-selection" | "close-button";
