    17→export type Width = "trigger" | string;
    18→export type Options = Pick<BaseProps, "width" | "fallbackAdjustLayout" | "fallbackMinWidth" | "fallbackMinHeight" | "contentGap" | "contentShift"> & {
    19→    position: Position;
    20→    fallbackPositions?: Position[];
    21→    container?: HTMLElement | null;
    22→    rtl: boolean;
    23→    lastUsedPosition: Position;
    24→    onPositionChoose: (position: Position) => void;
    25→};
    26→export type State = {
    27→    position?: Position;
    28→    status: "idle" | "rendered" | "positioned" | "visible" | "hidden";
    29→};
    30→export type FlyoutData = {
    31→    position: Position;
    32→};
    33→export type UseFlyoutData = Pick<State, "position" | "status"> & {
    34→    updatePosition: (args?: {
    35→        sync?: boolean;
