    82→    /** Unique id for the flyout content and trigger */
    83→    id?: string;
    84→    /** Event used for displaying the content */
    85→    triggerType?: "hover" | "click" | "focus";
    86→    /** Removes the content display delay if another flyout is already active */
    87→    groupTimeouts?: boolean;
    88→    /** Content position relative to the trigger element */
    89→    position?: Position;
    90→    /**
