    98→    /** Minimum width for the content when fallbackAdjustLayout is true */
    99→    fallbackMinWidth?: string;
   100→    /** Minimum height for the content when fallbackAdjustLayout is true */
   101→    fallbackMinHeight?: string;
   102→    /** Change component trap focus keyboard behavior and shortcuts */
   103→    trapFocusMode?: TrapMode | false;
   104→    /** Disable the flyout content interactivity */
