      <View gap={4} padding={4}>
        <ToggleButtonGroup
          value={tripType}
          onChange={({ value }) => setTripType(value)}
        >
          <ToggleButton value="oneway">One Way</ToggleButton>
          <ToggleButton value="return">Return</ToggleButton>
        </ToggleButtonGroup>

        <View gap={3}>
          <Autocomplete
            name="from"
            placeholder="From"
            value={fromValue}
            onInput={({ value }) => setFromInput(value)}
            onItemSelect={({ value, data }) => {
              setFromValue(value);
              setFromInput(data as string);
            }}
          >
            {filteredFromAirports.map((airport) => (
              <Autocomplete.Item
                key={airport.code}
                value={`${airport.code} – ${airport.name}`}
                data={`${airport.code} – ${airport.name}`}
              >
                {airport.code} – {airport.name}
              </Autocomplete.Item>
            ))}
          </Autocomplete>

          <Autocomplete
            name="to"
            placeholder="To"
            value={toValue}
            onInput={({ value }) => setToInput(value)}
            onItemSelect={({ value, data }) => {
              setToValue(value);
              setToInput(data as string);
            }}
          >
            {filteredToAirports.map((airport) => (
              <Autocomplete.Item
                key={airport.code}
                value={`${airport.code} – ${airport.name}`}
                data={`${airport.code} – ${airport.name}`}
              >
                {airport.code} – {airport.name}
              </Autocomplete.Item>
            ))}
          </Autocomplete>
        </View>

        <View direction="row" gap={3}>
          <Popover
            active={departureDateOpen}
            onOpen={() => setDepartureDateOpen(true)}
            onClose={() => setDepartureDateOpen(false)}
          >
            <Popover.Trigger>
              {(attributes) => (
                <Button {...attributes} variant="outline">
                  {formatDate(departureDate) || "Departure Date"}
                </Button>
              )}
            </Popover.Trigger>
            <Popover.Content>
              <Calendar
                value={departureDate}
                onChange={handleDepartureDateChange}
                min={today}
              />
            </Popover.Content>
          </Popover>

          {isReturn && (
            <Popover
              active={returnDateOpen}
              onOpen={() => setReturnDateOpen(true)}
              onClose={() => setReturnDateOpen(false)}
            >
              <Popover.Trigger>
                {(attributes) => (
                  <Button {...attributes} variant="outline">
                    {formatDate(returnDate) || "Return Date"}
                  </Button>
                )}
              </Popover.Trigger>
              <Popover.Content>
                <Calendar
                  value={returnDate}
                  onChange={handleReturnDateChange}
                  min={minReturnDate}
                />
              </Popover.Content>
            </Popover>
          )}
        </View>

        <Button color="primary" onClick={onSubmit}>
          Search Flights
        </Button>
      </View>
    </Reshaped>
  );
};

export default FlightBooking;
